from tkinter import *
def user_guide():
    user=Toplevel()
    user.geometry("1200x800+300+0")
    user.title("User Guide")
    user.configure(bg="#FFE9E3")    
    my_canvas=Canvas(user,width=1160)
    my_canvas.pack(side=LEFT,fill=Y,expand=1,padx=10)
    my_scrollbar=Scrollbar(user,orient=VERTICAL,command=my_canvas.yview)
    my_scrollbar.pack(side=RIGHT,fill=Y)
    my_canvas.configure(yscrollcommand=my_scrollbar.set)
    my_canvas.bind("<Configure>",lambda e:my_canvas.configure(scrollregion=my_canvas.bbox("all")))
    my_canvas.configure(bg="#FFE9E3")
    #my_canvas.pack(fill='both',expand=True)
    new_root=Frame(my_canvas)
    new_root.configure(bg="#FFE9E3")
    my_canvas.create_window((0,0),window=new_root,anchor="nw")
    head_font=('Times new Roman',18,'bold',"italic")   #Font style for headings
    body_font=('Times new Roman',15)
    spl_body_font=('Times new Roman',15,'bold')
    #Label(new_root,text="User Guide: SGPA and CGPA Calculation Tool",font=head_font,bg="#FFE9E3").grid(row=0,column=1,sticky='nw')
    Label(new_root,text="This user guide provides step-by-step instructions on how to use the Result Analysis tool to obtain and analyze ",font=body_font,bg="#FFE9E3").grid(row=1,column=1,sticky='nw')
    Label(new_root,text="student results for a semester.",font=body_font,bg="#FFE9E3").grid(row=2,column=1,sticky='nw')
    Label(new_root,text="Upload the University Result PDF",font=head_font,bg="#FFE9E3").grid(row=3,column=1,sticky='nw')
    Label(new_root,text="* Click on the 'Upload File' button. This will open a file dialog box.",font=body_font,bg="#FFE9E3").grid(row=4,column=1,sticky='nw')
    Label(new_root,text="* Locate and upload the result PDF file provided by the university on your local computer.",font=body_font,bg="#FFE9E3").grid(row=5,column=1,sticky='nw')
    Label(new_root,text="Generating Result Analysis",font=head_font,bg="#FFE9E3").grid(row=6,column=1,sticky='nw')
    Label(new_root,text="* After the PDF file has been successfully uploaded click on Result Analysis.",font=body_font,bg="#FFE9E3").grid(row=7,column=1,sticky='nw')
    Label(new_root,text="* The system will start analyzing the uploaded result PDF.",font=body_font,bg="#FFE9E3").grid(row=8,column=1,sticky='nw')
    Label(new_root,text="Save the Output File",font=head_font,bg="#FFE9E3").grid(row=9,column=1,sticky='nw')
    Label(new_root,text="* Once the analysis is complete, you'll be prompted to save the output file.",font=body_font,bg="#FFE9E3").grid(row=10,column=1,sticky='nw')
    Label(new_root,text="* Click on the 'Browse' button to choose a location on your computer where you want to save the output file.",font=body_font,bg="#FFE9E3").grid(row=11,column=1,sticky='nw')
    Label(new_root,text="* Select a folder or directory, give the file a suitable name, and click 'Save' in the file dialog box.",font=body_font,bg="#FFE9E3").grid(row=12,column=1,sticky='nw')
    Label(new_root,text="* Wait until it prompts 'Result Analysis file generation is completed'.",font=body_font,bg="#FFE9E3").grid(row=13,column=1,sticky='nw')
    Label(new_root,text="Handling Incorrect PDF Format",font=head_font,bg="#FFE9E3").grid(row=14,column=1,sticky='nw')
    Label(new_root,text="* If the uploaded PDF file is not in the correct format, the system will display an error message, such as",font=body_font,bg="#FFE9E3").grid(row=15,column=1,sticky='nw')
    Label(new_root,text='  "The uploaded PDF format is not suitable. Please try uploading an Excel file."',font=body_font,bg="#FFE9E3").grid(row=16,column=1,sticky='nw')
    Label(new_root,text="Points to Remember while Uploading Files:",font=spl_body_font,bg="#FFE9E3").grid(row=17,column=1,sticky='nw')
    Label(new_root,text="  * Ensure that the PDF file ends correctly (the line after credits should be closed), otherwise the file won't support",font=body_font,bg="#FFE9E3").grid(row=18,column=1,sticky='nw')
    Label(new_root,text="    the calculation. In such a case, convert the PDF into Excel and use it as input.",font=body_font,bg="#FFE9E3").grid(row=19,column=1,sticky='nw')    
    Label(new_root,text="  * If you use an online converter for converting PDF to Excel, ensure that the given input file only contains the",font=body_font,bg="#FFE9E3").grid(row=20,column=1,sticky='nw')
    Label(new_root,text="    grades table with Htno, Subcode, etc., as column names. Remove all the other things from the excel.",font=body_font,bg="#FFE9E3").grid(row=21,column=1,sticky='nw')
    Label(new_root,text="Review the Output",font=head_font,bg="#FFE9E3").grid(row=22,column=1,sticky='nw')
    Label(new_root,text="The obtained output file will contain 11 sheets:",font=spl_body_font,bg="#FFE9E3").grid(row=23,column=1,sticky='nw')
    Label(new_root,text="  * 5 sheets containing GPA information for all students and the number of students with backlogs branch-wise.",font=body_font,bg="#FFE9E3").grid(row=24,column=1,sticky='nw')
    Label(new_root,text="  * 5 sheets stores subject-wise pass percentages for each branch.",font=body_font,bg="#FFE9E3").grid(row=25,column=1,sticky='nw')
    Label(new_root,text="  * 1 sheet store branch-wise pass percentages and overall college pass percentage.",font=body_font,bg="#FFE9E3").grid(row=26,column=1,sticky='nw')
    